package com.basic;

public class FirstCode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//System.out.println("Hello World!");
		int a=1,b=2,c=3,d=5;
		System.out.println(a++);
		System.out.println(b++);
		System.out.println(c--);
		System.out.println(--d);
	}

}